/**
 * @description 图片数据
 * @example { width:700;height:700;again:false;success:true}
 * {success:false}
 */
export type ImageSize =
  | {
      width: number;
      height: number;
      again: boolean;
      success: true;
    }
  | {
      success: false;
    };
/**
 * @description 提前请求图片，获取图片的大小。
 */
export function getImageSize(imageSrc: string): Promise<ImageSize> {
  return new Promise((resolve, reject) => {
    const image = new Image();
    image.src = imageSrc;
    if (image.complete) {
      resolve({
        width: image.width,
        height: image.height,
        again: true,
        success: true,
      });
    }
    image.onload = () =>
      resolve({
        width: image.width,
        height: image.height,
        again: false,
        success: true,
      });
    image.onerror = () => {
      resolve({
        success: false,
      });
    };
  });
}

/**
 * @description 获取图片经过缩略的大小，默认提供fallback处理。返回数字为5的整数倍。
 */

export function getResizeHeight(imageSizeObj: ImageSize, realwidth: number) {
  const res = Math.ceil((100 + Math.ceil(Math.random() * 100)) / 5) * 5;
  if (
    imageSizeObj.success !== true ||
    imageSizeObj.width < 1 ||
    imageSizeObj.height < 1
  ) {
    return res;
  }
  const realheight =
    imageSizeObj.height / Math.floor(imageSizeObj.width / realwidth);
  return isNaN(realheight) ? res : Math.ceil(realheight / 5) * 5;
}

/**
 * @description 图片加载失败的fallback图片，已经过base64处理。
 */
export const fallbackUrl =
  "data:image/png;base64,";
